using Sortowania.Strategies;

namespace SortowanieTest
{
    [TestClass]
    public sealed class InsertSortStrategyIntegerTest
    {

        [TestMethod]
        public void ArrayIsSorted()
        {
            int[] array = { 5, 3, 1, 2, 4 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { 1, 2, 3, 4, 5 });
        }

        [TestMethod]
        public void ArrayIsUnsorted()
        {
            int[] array = { 1, 2, 3, 4, 5 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { 1, 2, 3, 4, 5 });
        }

        [TestMethod]
        public void ArrayIsReversedSorted()
        {
            int[] array = { 5, 4, 3, 2, 1 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { 1, 2, 3, 4, 5 });
        }

        [TestMethod]
        public void ArrayIsEmpty()
        {
            int[] array = new int[0];
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[0]);
        }

        [TestMethod]
        public void ArrayHasOneItem()
        {
            int[] array = { 1 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { 1 });
        }

        [TestMethod]
        public void ArrayHasDuplicates()
        {
            int[] array = { 1, 3, 2, 3, 5, 4 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { 1, 2, 3, 3, 4, 5 });
        }

        [TestMethod]
        public void ArrayOfIdenticalItems()
        {
            int[] array = { 1, 1, 1, 1 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { 1, 1, 1, 1 });
        }

        [TestMethod]
        public void ArrayWithNegativeNumbers()
        {
            int[] array = { 5, 2, 4, -1 };
            InsertSortStrategy<int> strategy = new InsertSortStrategy<int>();
            int[] result = strategy.Sort(array);

            CollectionAssert.AreEqual(result, new int[] { -1, 2, 4, 5 });
        }

